#'     }
#'  }
#' @rdname netStats-cpp
NetStats <- function(pCoexpD, discIndices, pCoexpT, testIndices) {
    .Call('netrep_NetStats', PACKAGE = 'netrep', pCoexpD, discIndices, pCoexpT, testIndices)
}

